document.addEventListener("DOMContentLoaded",(()=>{const e=document.querySelector(".navbar"),t=document.querySelector(".scroll-to-top");window.addEventListener("scroll",(function(){window.scrollY>50?(e.classList.add("scrolled"),t?.classList.add("visible")):(e.classList.remove("scrolled"),t?.classList.remove("visible"))}));const o=document.querySelectorAll("section[id]"),c=document.querySelectorAll(".nav-link");window.addEventListener("scroll",(function(){const e=window.pageYOffset;o.forEach((t=>{const o=t.offsetHeight,s=t.offsetTop-100,n=t.getAttribute("id"),l=document.querySelector(`.nav-link[href*='${n}']`);e>s&&e<=s+o&&(c.forEach((e=>e.classList.remove("active"))),l?.classList.add("active"))}))})),document.querySelectorAll('a[href^="#"]').forEach((e=>{e.addEventListener("click",(function(e){e.preventDefault();const t=document.querySelector(this.getAttribute("href"));if(t){t.scrollIntoView({behavior:"smooth",block:"start"});const e=document.querySelector(".navbar-toggler");document.querySelector(".navbar-collapse").classList.contains("show")&&e.click()}}))}))}));